  const [activeButtonID, setactiveButtonID] = useState(0);

  setactiveButtonID(buttonID);

  return activeButtonID;
} */

function PageTransition(clickedButtonID: number) {
/*     const [activeButtonID, setactiveButtonID] = useState(0);

    setactiveButtonID(clickedButtonID); */

    const clickedButtonControlNumber: string = "control-" + clickedButtonID.toString();

    let currentButton = document.querySelectorAll(".active-btn");
    let clickedButton = document.querySelectorAll(clickedButtonControlNumber);

    console.log(currentButton[0].className);
    console.log(" jaja ")
    console.log(clickedButton[0].className);

    currentButton[0].className = currentButton[0].className.replace(
      " active-btn",
      ""
    );

    clickedButton[0].className = clickedButton[0].className.replace(
        "",
        " active-btn"
      );
}

export default PageTransition;
