import React, { useState } from "react";

const sections = document.querySelectorAll(".section");
const sectionAllButtons = document.querySelectorAll(".controls");
const sectionButton = document.querySelectorAll(".control");
const allSections = document.querySelector(".main-content");

/* function PageTransition() {
  for (let i = 0; i < sectionButton.length; i++) {
    sectionButton[i].addEventListener("click", function () {
      let currentButton = document.querySelectorAll(".active-btn");
      currentButton[0].className = currentButton[0].className.replace(
        " active-btn",
        ""
      );
      currentButton[3].className += " active-btn";
    });
  }
} */
function UpdateActiveButton(buttonID: number) {
  const [activeButtonID, setactiveButtonID] = useState(0);

  setactiveButtonID(buttonID);

  return activeButtonID;
}

function PageTransition(clickedButtonID: number) {
    let currentButton = document.querySelectorAll(".active-btn");
    let clickedButton = document.querySelectorAll("control-" + UpdateActiveButton(clickedButtonID));

    currentButton[0].className = currentButton[0].className.replace(
      " active-btn",
      ""
    );

    clickedButton[0].className = clickedButton[0].className.concat(" active-btn");
}

export default PageTransition;
